import React, { useState } from "react";

export default function Dashboard() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!query.trim()) return;

    setLoading(true);
    setError(null);

    try {
      // Using Yahoo Finance autocomplete API for search suggestions:
      // This endpoint returns company matches for your query.
      const response = await fetch(
        `https://query2.finance.yahoo.com/v1/finance/search?q=${encodeURIComponent(query)}`
      );

      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }

      const data = await response.json();

      // data.quotes contains the matched companies/tickers
      setResults(data.quotes || []);
    } catch (err) {
      setError("Error fetching company data.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 600, margin: "auto" }}>
      <h1 style={{ marginBottom: 20 }}>Dashboard</h1>

      <form onSubmit={handleSearch} style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Search company or ticker"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={{
            width: "100%",
            padding: 10,
            fontSize: 16,
            borderRadius: 5,
            border: "1px solid #ccc",
          }}
        />
      </form>

      {loading && <p>Loading...</p>}

      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && !error && results.length > 0 && (
        <ul style={{ listStyle: "none", paddingLeft: 0 }}>
          {results.map((item) => (
            <li
              key={item.symbol}
              style={{
                marginBottom: 15,
                padding: 10,
                border: "1px solid #ddd",
                borderRadius: 5,
              }}
            >
              <strong>{item.shortname || item.symbol}</strong> ({item.symbol})<br />
              {item.exchDisp && <small>Exchange: {item.exchDisp}</small>}
            </li>
          ))}
        </ul>
      )}

      {!loading && !error && results.length === 0 && query && (
        <p>No results found.</p>
      )}
    </div>
  );
}
