import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (res.ok) {
        navigate('/dashboard');
      } else {
        alert(`❌ ${data.error}`);
      }
    } catch (err) {
      console.error(err);
      alert('❌ Login failed.');
    }
  };

  return (
    <form onSubmit={handleLogin} className="space-y-6 animate-fadeIn">
      <h2 className="text-3xl font-bold text-blue-700 mb-4 text-center">Login</h2>

      <div className="relative">
        <input
          type="email"
          placeholder="Email"
          className="w-full border border-blue-400 rounded-md px-12 py-3 focus:outline-none focus:ring-2 focus:ring-blue-600 transition"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <svg
          className="w-6 h-6 absolute left-3 top-3 text-blue-600"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" d="M16 12H8m8 0l-4 4m4-4l-4-4"></path>
        </svg>
      </div>

      <div className="relative">
        <input
          type="password"
          placeholder="Password"
          className="w-full border border-blue-400 rounded-md px-12 py-3 focus:outline-none focus:ring-2 focus:ring-blue-600 transition"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <svg
          className="w-6 h-6 absolute left-3 top-3 text-blue-600"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" d="M12 11c1.38 0 2.5-1.12 2.5-2.5S13.38 6 12 6 9.5 7.12 9.5 8.5 10.62 11 12 11z"></path>
          <path strokeLinecap="round" strokeLinejoin="round" d="M12 14v2"></path>
        </svg>
      </div>

      <button
        type="submit"
        className="w-full py-3 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 shadow-lg transition transform hover:-translate-y-1"
      >
        Login
      </button>
    </form>
  );
}
