// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import Dashboard from './Dashboard';

export default function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50 flex flex-col items-center p-6 bg-cover bg-center" style={{ backgroundImage: "url('/assets/auth-bg.jpg')" }}>
        <h1 className="text-4xl font-bold text-white shadow mb-6">Auth Portal</h1>

        {/* Navigation */}
        <div className="mb-8 space-x-6">
          <Link to="/login" className="text-white bg-blue-600 px-4 py-2 rounded hover:bg-blue-700">Login</Link>
          <Link to="/register" className="text-white bg-green-600 px-4 py-2 rounded hover:bg-green-700">Register</Link>
        </div>

        {/* Routes */}
        <div className="w-full max-w-md">
          <Routes>
            <Route path="/" element={<Navigate to="/login" />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/dashboard" element={<Dashboard />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}
