import React, { useState, useEffect } from "react";

export default function Dashboard() {
  const [query, setQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState("");

  // Fetch all watchlists
  const fetchWatchlists = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/watchlists/");
      const data = await res.json();
      setWatchlists(data);
    } catch (error) {
      console.error("Error fetching watchlists:", error);
    }
  };

  useEffect(() => {
    fetchWatchlists();
  }, []);

  // Global search
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (value.length < 2) return setSearchResults([]);
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`);
      const data = await res.json();
      setSearchResults(data);
    } catch (error) {
      console.error("Error searching companies:", error);
    }
  };

  // Create a new watchlist
  const handleCreateWatchlist = async () => {
    if (!newWatchlistName) return;
    try {
      await fetch("http://localhost:5000/api/watchlists/create/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      setNewWatchlistName("");
      fetchWatchlists();
    } catch (error) {
      console.error("Error creating watchlist:", error);
    }
  };

  // Add company to a watchlist
  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/add/`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(company),
      });
      fetchWatchlists();
    } catch (error) {
      console.error("Error adding company to watchlist:", error);
    }
  };

  // Remove company from a watchlist
  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: "DELETE",
      });
      fetchWatchlists();
    } catch (error) {
      console.error("Error removing company from watchlist:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dashboard</h1>

      {/* Top Search Bar */}
      <div style={{ marginBottom: "30px" }}>
        <input
          type="text"
          placeholder="Search companies..."
          value={query}
          onChange={handleSearch}
          style={{ padding: "5px", width: "400px" }}
        />
        {searchResults.length > 0 && (
          <ul
            style={{
              border: "1px solid #ccc",
              maxHeight: "200px",
              overflowY: "auto",
              marginTop: "5px",
              width: "400px",
              background: "#fff",
              position: "absolute",
              zIndex: 1,
            }}
          >
            {searchResults.map((c, i) => (
              <li key={i} style={{ padding: "5px", borderBottom: "1px solid #eee" }}>
                {c.symbol} - {c.name}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Watchlist Management */}
      <div style={{ marginBottom: "30px" }}>
        <h2>Watchlists</h2>
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="New watchlist name"
            value={newWatchlistName}
            onChange={(e) => setNewWatchlistName(e.target.value)}
          />
          <button onClick={handleCreateWatchlist} style={{ marginLeft: "5px" }}>
            + Add Watchlist
          </button>
        </div>

        {/* Display Watchlists */}
        {watchlists.map((wl) => (
          <div
            key={wl.id}
            style={{
              border: "1px solid #ccc",
              padding: "10px",
              marginBottom: "10px",
              borderRadius: "5px",
            }}
          >
            <h3>{wl.name}</h3>
            <ul>
              {wl.items.map((item) => (
                <li
                  key={item.id}
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "5px",
                    borderBottom: "1px solid #eee",
                  }}
                >
                  <span>
                    {item.symbol} - {item.name}
                  </span>
                  <button onClick={() => handleRemoveFromWatchlist(wl.id, item.id)}>
                    Remove
                  </button>
                </li>
              ))}
            </ul>

            {/* Add company to this watchlist */}
            <div style={{ marginTop: "5px" }}>
              <label>Add company to this watchlist:</label>
              <ul>
                {searchResults.map((c, idx) => (
                  <li key={idx} style={{ display: "flex", justifyContent: "space-between" }}>
                    <span>
                      {c.symbol} - {c.name}
                    </span>
                    <button onClick={() => handleAddToWatchlist(wl.id, c)}>+</button>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
