import React, { useState, useEffect } from "react";

export default function Dashboard() {
  const [query, setQuery] = useState("");
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [watchlist, setWatchlist] = useState([]);

  const token = localStorage.getItem("token"); // JWT token from login

  // ðŸ”¹ Fetch watchlist from backend
  useEffect(() => {
    const fetchWatchlist = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/watchlist/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        setWatchlist(data);
      } catch (err) {
        console.error("Error fetching watchlist:", err);
      }
    };
  
    fetchWatchlist();
  }, [token]); // only runs when token changes

  // ðŸ” Search for companies
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (value.length > 1) {
      try {
        const res = await fetch(
          `http://localhost:5000/api/search-stock/?q=${value}`
        );
        const data = await res.json();
        setCompanies(data);
      } catch (err) {
        console.error(err);
      }
    } else {
      setCompanies([]);
    }
  };

  // ðŸ“Š Fetch company details
  const handleSelectCompany = async (symbol) => {
    setQuery("");
    setCompanies([]);
    try {
      const res = await fetch(
        `http://localhost:5000/api/company/${symbol}/`
      );
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  // âž• Add company to watchlist
  const handleAddToWatchlist = async (company) => {
    try {
      const res = await fetch("http://localhost:5000/api/watchlist/add/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(company),
      });
      if (res.ok) {
        fetchWatchlist();
      } else {
        const errData = await res.json();
        alert(errData.message || "Failed to add to watchlist");
      }
    } catch (err) {
      console.error(err);
    }
  };

  // âŒ Remove from watchlist
  const handleRemoveFromWatchlist = async (symbol) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/watchlist/remove/${symbol}/`,
        {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      if (res.ok) {
        fetchWatchlist();
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dashboard</h1>

      {/* Search input */}
      <input
        type="text"
        placeholder="Search for a company..."
        value={query}
        onChange={handleSearch}
        style={{ padding: "10px", width: "300px" }}
      />

      {/* Dropdown list */}
      {companies.length > 0 && (
        <ul
          style={{
            border: "1px solid #ccc",
            borderRadius: "4px",
            marginTop: "5px",
            maxHeight: "200px",
            overflowY: "auto",
            width: "300px",
            background: "#fff",
            position: "absolute",
            zIndex: 1000,
          }}
        >
          {companies.map((c, index) => (
            <li
              key={index}
              style={{
                padding: "8px",
                cursor: "pointer",
                display: "flex",
                justifyContent: "space-between",
              }}
            >
              <span onClick={() => handleSelectCompany(c.symbol)}>
                {c.symbol} - {c.name} ({c.exchange})
              </span>
              <button onClick={() => handleAddToWatchlist(c)}>+ Watchlist</button>
            </li>
          ))}
        </ul>
      )}

      {/* Company details */}
      {selectedCompany && selectedCompany.price && (
        <div style={{ marginTop: "30px" }}>
          <h2>
            {selectedCompany.price.longName} ({selectedCompany.price.symbol})
          </h2>
          <p>
            <strong>Exchange:</strong> {selectedCompany.price.exchangeName}
          </p>
          <p>
            <strong>Current Price:</strong> $
            {selectedCompany.price.regularMarketPrice}
          </p>
          <p>
            <strong>Sector:</strong>{" "}
            {selectedCompany.summaryProfile?.sector}
          </p>
          <p>
            <strong>Industry:</strong>{" "}
            {selectedCompany.summaryProfile?.industry}
          </p>
          <p>
            <strong>Description:</strong>{" "}
            {selectedCompany.summaryProfile?.longBusinessSummary}
          </p>
        </div>
      )}

      {/* Watchlist */}
      <div style={{ marginTop: "40px" }}>
        <h2>My Watchlist</h2>
        {watchlist.length === 0 ? (
          <p>No companies in watchlist</p>
        ) : (
          <ul>
            {watchlist.map((item) => (
              <li
                key={item.id}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  width: "300px",
                  padding: "5px 0",
                }}
              >
                <span>
                  {item.symbol} - {item.name} ({item.exchange})
                </span>
                <button
                  onClick={() => handleRemoveFromWatchlist(item.symbol)}
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
