import React, { useState, useEffect } from "react";

const Dashboard = ({ token, userId }) => {
  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlist, setNewWatchlist] = useState("");
  const [selectedWatchlist, setSelectedWatchlist] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedSector, setSelectedSector] = useState("Technology");
  const [addingRandom, setAddingRandom] = useState(false);

  const sectors = [
    "Communication Services",
    "Consumer Discretionary",
    "Consumer Staples",
    "Energy",
    "Financials",
    "Health Care",
    "Industrials",
    "Information Technology",
    "Materials",
    "Real Estate",
    "Utilities",
  ];

  // --------------------
  // Fetch watchlists
  // --------------------
  useEffect(() => {
    fetch("http://localhost:5000/api/watchlists/", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        // Ensure it's always an array
        if (Array.isArray(data)) {
          setWatchlists(data);
        } else if (data.results) {
          // In case it's paginated response from DRF
          setWatchlists(data.results);
        } else {
          setWatchlists([]);
          console.error("Unexpected response format:", data);
        }
      })
      .catch((err) => console.error("Error fetching watchlists:", err));
  }, [token]);
  

  // --------------------
  // Create watchlist
  // --------------------
  const handleCreateWatchlist = async (e) => {
    e.preventDefault();
    if (!newWatchlist.trim()) return;
    const res = await fetch("http://localhost:5000/api/watchlists/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ name: newWatchlist }),
    });
    const data = await res.json();
    setWatchlists([...watchlists, data]);
    setNewWatchlist("");
  };

  // --------------------
  // Search companies
  // --------------------
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;
    const res = await fetch(
      `http://localhost:5000/api/search/?q=${searchQuery}`,
      {
        headers: { Authorization: `Bearer ${token}` },
      }
    );
    const data = await res.json();
    setSearchResults(data);
  };

  // --------------------
  // Add company to watchlist
  // --------------------
  const handleAddToWatchlist = async (company) => {
    if (!selectedWatchlist) return alert("Select a watchlist first");
    const res = await fetch(
      `http://localhost:5000/api/watchlists/${selectedWatchlist}/add/`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(company),
      }
    );
    const data = await res.json();
    setWatchlists(
      watchlists.map((wl) =>
        wl.id === selectedWatchlist
          ? { ...wl, items: [...wl.items, data] }
          : wl
      )
    );
  };

  // --------------------
  // Remove company
  // --------------------
  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    await fetch(
      `http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`,
      {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      }
    );
    setWatchlists(
      watchlists.map((wl) =>
        wl.id === watchlistId
          ? { ...wl, items: wl.items.filter((item) => item.id !== itemId) }
          : wl
      )
    );
  };

  // --------------------
  // Add 10 random companies by sector
  // --------------------
  const handleAddRandomBySector = async () => {
    if (!selectedWatchlist) {
      alert("Please choose a watchlist first!");
      return;
    }
    if (addingRandom) return; // guard
    setAddingRandom(true);
    try {
      const res = await fetch(
        `http://localhost:5000/api/watchlists/${selectedWatchlist}/add-random/`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ sector: selectedSector }),
        }
      );
      const data = await res.json();
      if (res.ok) {
        setWatchlists((prev) =>
          prev.map((wl) => (wl.id === selectedWatchlist ? data : wl))
        );
      } else {
        alert(data.error || "Failed to add random companies.");
      }
    } catch (err) {
      console.error(err);
      alert("Request failed. Please try again.");
    } finally {
      setAddingRandom(false);
    }
  };

  // --------------------
  // Render
  // --------------------
  return (
    <div style={{ display: "flex" }}>
      {/* Sidebar */}
      <div style={{ width: "250px", borderRight: "1px solid #ccc", padding: "10px" }}>
        <h3>Watchlists</h3>
        <form onSubmit={handleCreateWatchlist}>
          <input
            placeholder="New Watchlist"
            value={newWatchlist}
            onChange={(e) => setNewWatchlist(e.target.value)}
          />
          <button type="submit">Create</button>
        </form>
        <ul>
          {watchlists.map((wl) => (
            <li key={wl.id}>
              <button onClick={() => setSelectedWatchlist(wl.id)}>
                {wl.name}
              </button>
            </li>
          ))}
        </ul>
        <hr />
        <h4>Add Random by Sector</h4>
        <select
          value={selectedSector}
          onChange={(e) => setSelectedSector(e.target.value)}
        >
          {sectors.map((s) => (
            <option key={s} value={s}>
              {s}
            </option>
          ))}
        </select>
        <button
          type="button"
          onClick={handleAddRandomBySector}
          disabled={addingRandom || !selectedWatchlist}
          style={{
            marginTop: "10px",
            opacity: addingRandom || !selectedWatchlist ? 0.6 : 1,
          }}
        >
          {addingRandom ? "Adding..." : "➕ Add 10 Random Companies"}
        </button>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: "10px" }}>
        <h2>Search Companies</h2>
        <form onSubmit={handleSearch}>
          <input
            placeholder="Search for companies"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
        <ul>
          {searchResults.map((c) => (
            <li key={c.symbol}>
              {c.symbol} - {c.name} ({c.exchange})
              <button onClick={() => handleAddToWatchlist(c)}>Add</button>
            </li>
          ))}
        </ul>

        <h2>Selected Watchlist</h2>
        {selectedWatchlist &&
          watchlists
            .find((wl) => wl.id === selectedWatchlist)
            ?.items.map((item) => (
              <li key={item.id}>
                {item.symbol} - {item.name} ({item.exchange})
                <button
                  onClick={() =>
                    handleRemoveFromWatchlist(selectedWatchlist, item.id)
                  }
                >
                  ❌
                </button>
              </li>
            ))}
      </div>
    </div>
  );
};

export default Dashboard;
