import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSearch, FaPlus, FaTrash } from 'react-icons/fa';

export default function Dashboard() {
  const [query, setQuery] = useState('');
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState('');
  const [watchlistSearch, setWatchlistSearch] = useState({});
  const [watchlistSearchResults, setWatchlistSearchResults] = useState({});
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) navigate('/login');
  }, [token, navigate]);

  useEffect(() => {
    fetchWatchlists();
  }, []);

  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (!value) return setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setCompanies(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSelectCompany = async (symbol) => {
    setQuery('');
    setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/company/${symbol}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchWatchlists = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setWatchlists(data.map(wl => ({ ...wl, items: wl.items || [] })));
    } catch (err) {
      console.error(err);
    }
  };

  const handleCreateWatchlist = async () => {
    if (!newWatchlistName.trim()) return;
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/create/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      const newWatchlist = await res.json();
      setWatchlists(prev => [...prev, { ...newWatchlist, items: [] }]);
      setNewWatchlistName('');
    } catch (err) {
      console.error(err);
    }
  };

  const handleWatchlistSearch = async (watchlistId, value) => {
    setWatchlistSearch(prev => ({ ...prev, [watchlistId]: value }));
    if (!value) return setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: data }));
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/add/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(company),
      });
      const addedCompany = await res.json();
      setWatchlists(prev =>
        prev.map(wl => (wl.id === watchlistId ? { ...wl, items: [...wl.items, addedCompany] } : wl))
      );
      setWatchlistSearch(prev => ({ ...prev, [watchlistId]: '' }));
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    } catch (err) {
      console.error(err);
    }
  };

  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      setWatchlists(prev =>
        prev.map(wl => (wl.id === watchlistId ? { ...wl, items: wl.items.filter(item => item.id !== itemId) } : wl))
      );
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-900 to-blue-800 text-white flex flex-col items-center p-6">
      <h1 className="text-4xl font-bold mb-6">📈 My Dashboard</h1>

      {/* Global Search */}
      <div className="relative mb-8 w-full max-w-md">
        <div className="flex items-center bg-blue-700 rounded-lg p-2">
          <FaSearch className="text-white mr-2" />
          <input
            type="text"
            placeholder="Search for a company..."
            value={query}
            onChange={handleSearch}
            className="bg-blue-700 text-white placeholder-gray-300 focus:outline-none flex-1 p-2 rounded-lg"
          />
        </div>
        {companies.length > 0 && (
          <ul className="absolute top-12 left-0 w-full bg-blue-600 rounded-lg max-h-60 overflow-y-auto z-50 shadow-lg">
            {companies.map((c, index) => (
              <li
                key={index}
                onClick={() => handleSelectCompany(c.symbol)}
                className="cursor-pointer p-2 hover:bg-blue-500 transition-colors"
              >
                {c.symbol} - {c.name} ({c.exchange})
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Company Details */}
      {selectedCompany?.price && (
        <div className="bg-blue-700 rounded-lg p-6 w-full max-w-3xl mb-8 shadow-lg">
          <h2 className="text-2xl font-semibold mb-2">{selectedCompany.price.longName} ({selectedCompany.price.symbol})</h2>
          <p><strong>Exchange:</strong> {selectedCompany.price.exchangeName}</p>
          <p><strong>Current Price:</strong> ${selectedCompany.price.regularMarketPrice}</p>
          <p><strong>Sector:</strong> {selectedCompany.summaryProfile?.sector}</p>
          <p><strong>Industry:</strong> {selectedCompany.summaryProfile?.industry}</p>
          <p className="mt-2"><strong>Description:</strong> {selectedCompany.summaryProfile?.longBusinessSummary}</p>
        </div>
      )}

      {/* Watchlists */}
      <div className="w-full max-w-4xl">
        <h2 className="text-3xl font-bold mb-4">📂 Watchlists</h2>
        <div className="flex mb-4">
          <input
            type="text"
            placeholder="New watchlist name"
            value={newWatchlistName}
            onChange={(e) => setNewWatchlistName(e.target.value)}
            className="flex-1 p-2 rounded-l-lg bg-blue-700 text-white placeholder-gray-300 focus:outline-none"
          />
          <button onClick={handleCreateWatchlist} className="bg-green-500 px-4 rounded-r-lg hover:bg-green-600 transition-colors flex items-center">
            <FaPlus className="mr-1" /> Add
          </button>
        </div>

        {watchlists.map(wl => (
          <div key={wl.id} className="bg-blue-700 rounded-lg p-4 mb-4 shadow-md">
            <h3 className="text-xl font-semibold mb-2">{wl.name}</h3>

            {/* Mini Search */}
            <div className="relative mb-2 w-full max-w-md">
              <input
                type="text"
                placeholder="Search company to add..."
                value={watchlistSearch[wl.id] || ''}
                onChange={(e) => handleWatchlistSearch(wl.id, e.target.value)}
                className="w-full p-2 rounded-lg bg-blue-600 placeholder-gray-300 focus:outline-none"
              />
              {watchlistSearchResults[wl.id]?.length > 0 && (
                <ul className="absolute top-10 w-full bg-blue-500 rounded-lg max-h-48 overflow-y-auto shadow-lg z-50">
                  {watchlistSearchResults[wl.id].map((c, idx) => (
                    <li key={idx} className="flex justify-between p-2 hover:bg-blue-400 cursor-pointer">
                      <span>{c.symbol} - {c.name}</span>
                      <button onClick={() => handleAddToWatchlist(wl.id, c)} className="bg-green-500 px-2 rounded hover:bg-green-600">
                        <FaPlus />
                      </button>
                    </li>
                  ))}
                </ul>
              )}
            </div>

            {/* Items */}
            <ul className="mt-2">
              {wl.items.map(item => (
                <li key={item.id} className="flex justify-between p-2 border-b border-blue-500">
                  <span>{item.symbol} - {item.name}</span>
                  <button onClick={() => handleRemoveFromWatchlist(wl.id, item.id)} className="bg-red-500 px-2 rounded hover:bg-red-600 flex items-center">
                    <FaTrash />
                  </button>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
