// src/Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FiTrendingUp, FiTrendingDown, FiSearch, FiPlus, FiTrash2, FiList, FiBriefcase } from "react-icons/fi";
import "./Dashboard.css";

export default function Dashboard() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [query, setQuery] = useState("");
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState("");
  const [watchlistSearch, setWatchlistSearch] = useState({});
  const [watchlistSearchResults, setWatchlistSearchResults] = useState({});

  const [sectors, setSectors] = useState([]);
  const [selectedSector, setSelectedSector] = useState("");
  const [selectedWatchlist, setSelectedWatchlist] = useState(null);
  const [addingRandom, setAddingRandom] = useState(false);

  // Redirect if not logged in
  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  useEffect(() => {
    fetchWatchlists();
    fetchSectors();
  }, []);

  const fetchWatchlists = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/watchlists/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
  
      let wlArray = [];
      if (Array.isArray(data)) wlArray = data;
      else if (Array.isArray(data.results)) wlArray = data.results;
      else if (Array.isArray(data.watchlists)) wlArray = data.watchlists;
  
      // Show watchlists immediately (even if items donâ€™t have prices yet)
      setWatchlists(wlArray.map((wl) => ({ ...wl, items: wl.items || [] })));
  
      // Fetch live prices in background
      wlArray.forEach((wl) => {
        wl.items.forEach(async (item) => {
          try {
            const res = await fetch(`http://localhost:5000/api/company/${encodeURIComponent(item.symbol)}/`, {
              headers: { Authorization: `Bearer ${token}` },
            });
            const data = await res.json();
            setWatchlists((prev) =>
              prev.map((w) =>
                w.id === wl.id
                  ? {
                      ...w,
                      items: w.items.map((it) =>
                        it.id === item.id
                          ? {
                              ...it,
                              current_price: data.price.regularMarketPrice,
                              change: data.price.regularMarketChange,
                              change_percent: data.price.regularMarketChangePercent,
                            }
                          : it
                      ),
                    }
                  : w
              )
            );
          } catch (err) {
            console.error("Price fetch failed for", item.symbol, err);
          }
        });
      });
    } catch (err) {
      console.error("Failed to fetch watchlists", err);
    }
  };
  
  

  const fetchSectors = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/sectors/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (Array.isArray(data) && data.length > 0) {
        setSectors(data);
        setSelectedSector(data[0]);
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (!value) {
      setCompanies([]);
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${encodeURIComponent(value)}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setCompanies(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSelectCompany = async (symbol) => {
    setQuery("");
    setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/company/${encodeURIComponent(symbol)}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleCreateWatchlist = async () => {
    if (!newWatchlistName.trim()) return;
    try {
      const res = await fetch("http://localhost:5000/api/watchlists/create/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      const data = await res.json();
      setWatchlists((prev) => [...prev, { ...data, items: data.items || [] }]);
      setNewWatchlistName("");
    } catch (err) {
      console.error(err);
    }
  };

  const handleWatchlistSearch = async (watchlistId, value) => {
    setWatchlistSearch((prev) => ({ ...prev, [watchlistId]: value }));
    if (!value) return setWatchlistSearchResults((prev) => ({ ...prev, [watchlistId]: [] }));
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${encodeURIComponent(value)}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setWatchlistSearchResults((prev) => ({ ...prev, [watchlistId]: Array.isArray(data) ? data : [] }));
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/add/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(company),
      });
      const addedCompany = await res.json();
      setWatchlists((prev) =>
        prev.map((wl) =>
          wl.id === watchlistId ? { ...wl, items: [...(wl.items || []), addedCompany] } : wl
        )
      );
      setWatchlistSearch((prev) => ({ ...prev, [watchlistId]: "" }));
      setWatchlistSearchResults((prev) => ({ ...prev, [watchlistId]: [] }));
    } catch (err) {
      console.error(err);
    }
  };

  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      setWatchlists((prev) =>
        prev.map((wl) =>
          wl.id === watchlistId ? { ...wl, items: (wl.items || []).filter((it) => it.id !== itemId) } : wl
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddRandomBySector = async () => {
    if (!selectedWatchlist) return alert("Select a watchlist first!");
    if (addingRandom) return;
    setAddingRandom(true);
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${selectedWatchlist}/add-random/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sector: selectedSector }),
      });
      const data = await res.json();
      if (res.ok) {
        setWatchlists((prev) => prev.map((wl) => (wl.id === selectedWatchlist ? data : wl)));
      } else {
        alert(data.error || "Failed to add random companies.");
      }
    } catch (err) {
      console.error(err);
    } finally {
      setAddingRandom(false);
    }
  };

  return (
    <div className="dashboard-layout">
      {/* Side panel */}
      <aside className="side-panel">
        <h2>ðŸŽ¯ Quick Actions</h2>

        <label>Choose Sector</label>
        <select value={selectedSector} onChange={(e) => setSelectedSector(e.target.value)}>
          {sectors.map((s) => (
            <option key={s} value={s}>{s}</option>
          ))}
        </select>

        <label>Choose Watchlist</label>
        <select value={selectedWatchlist || ""} onChange={(e) => setSelectedWatchlist(Number(e.target.value))}>
          <option value="">-- Select Watchlist --</option>
          {watchlists.map((wl) => (
            <option key={wl.id} value={wl.id}>{wl.name}</option>
          ))}
        </select>

        <button onClick={handleAddRandomBySector} disabled={addingRandom || !selectedWatchlist}>
          {addingRandom ? "Adding..." : "âž• Add 10 Random"}
        </button>
      </aside>

      {/* Main area */}
      <main className="dashboard-container">
        <h1>ðŸ“Š My Finance Dashboard</h1>

        {/* Global Search */}
        <div className="card search-card">
          <div className="search-bar">
            <FiSearch className="icon" />
            <input
              type="text"
              placeholder="Search for a company..."
              value={query}
              onChange={handleSearch}
            />
          </div>
          {companies.length > 0 && (
            <ul className="search-results">
              {companies.map((c, idx) => (
                <li key={idx} onClick={() => handleSelectCompany(c.symbol)}>
                  <FiBriefcase /> {c.symbol} - {c.name}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Company Details */}
        {selectedCompany?.price && (
          <div className="card company-card">
            <h2>{selectedCompany.price.longName} ({selectedCompany.price.symbol})</h2>
            <p><strong>Exchange:</strong> {selectedCompany.price.exchangeName}</p>
            <p><strong>Current Price:</strong> ${selectedCompany.price.regularMarketPrice}</p>
            <p><strong>Sector:</strong> {selectedCompany.summaryProfile?.sector}</p>
            <p><strong>Industry:</strong> {selectedCompany.summaryProfile?.industry}</p>
            <p><strong>Description:</strong> {selectedCompany.summaryProfile?.longBusinessSummary}</p>
          </div>
        )}

        {/* Watchlists */}
        <div className="card">
          <h2><FiList /> Watchlists</h2>
          <div className="create-watchlist">
            <input
              type="text"
              placeholder="New watchlist name"
              value={newWatchlistName}
              onChange={(e) => setNewWatchlistName(e.target.value)}
            />
            <button onClick={handleCreateWatchlist}><FiPlus /> Add</button>
          </div>

          {watchlists.map((wl) => (
            <div key={wl.id} className="card watchlist-card">
              <h3>{wl.name}</h3>

              {/* Mini Search */}
              <div className="mini-search">
                <FiSearch className="icon" />
                <input
                  type="text"
                  placeholder="Search company to add..."
                  value={watchlistSearch[wl.id] || ""}
                  onChange={(e) => handleWatchlistSearch(wl.id, e.target.value)}
                />
              </div>
              {watchlistSearchResults[wl.id]?.length > 0 && (
                <ul className="search-results">
                  {watchlistSearchResults[wl.id].map((c, idx) => (
                    <li key={idx}>
                      {c.symbol} - {c.name}
                      <button onClick={() => handleAddToWatchlist(wl.id, c)}><FiPlus /></button>
                    </li>
                  ))}
                </ul>
              )}

              {/* Items */}
              <ul className="watchlist-items">
                {(wl.items || []).map((item) => (
                  <li key={item.id}>
                    <div className="stock-info">
                      <strong>{item.symbol}</strong> - {item.name}
                      {item.current_price !== undefined && (
                        <span className={`price-change ${item.change >= 0 ? "up" : "down"}`}>
                          ðŸ’µ ${item.current_price.toFixed(2)}
                          {item.change >= 0 ? <FiTrendingUp /> : <FiTrendingDown />}
                          {item.change.toFixed(2)} ({item.change_percent.toFixed(2)}%)
                        </span>
                      )}
                    </div>
                    <button className="remove-btn" onClick={() => handleRemoveFromWatchlist(wl.id, item.id)}>
                      <FiTrash2 />
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
