// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import Dashboard from './Dashboard';

export default function App() {
  return (
    <Router>
      <div
      className="w-full h-screen flex flex-col items-center justify-center bg-cover bg-center bg-no-repeat relative"
      style={{ backgroundImage: "url('/assets/auth-bg.jpg')", backgroundColor: 'rebeccapurple' }}
      >


        {/* Overlay */}
        <div className="absolute inset-0 bg-black/50 z-0"></div>

        <div className="z-10 text-center mb-8">
          <h1 className="text-5xl font-extrabold text-white drop-shadow-lg">ðŸŒˆ Auth Portal</h1>
          <p className="text-white text-lg mt-2">Secure access, beautifully crafted</p>
        </div>

        {/* Navigation */}
        <div className="z-10 mb-8 space-x-6">
          <Link to="/login" className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white px-6 py-2 rounded-full shadow-lg hover:scale-105 transform transition duration-300">
            Login
          </Link>
          <Link to="/register" className="bg-gradient-to-r from-green-500 to-emerald-600 text-white px-6 py-2 rounded-full shadow-lg hover:scale-105 transform transition duration-300">
            Register
          </Link>
        </div>

        {/* Routes */}
        <div className="z-10 w-full max-w-md">
          <Routes>
            <Route path="/" element={<Navigate to="/login" />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/dashboard" element={<Dashboard />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}
