import React, { useState, useEffect } from 'react';

const API = 'http://localhost:5000/api';

function authHeaders() {
  const token = localStorage.getItem('access');
  return token ? { Authorization: `Bearer ${token}` } : {};
}

export default function Dashboard() {
  const [query, setQuery] = useState('');
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  // Watchlist states
  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState('');
  const [watchlistSearch, setWatchlistSearch] = useState({});
  const [watchlistSearchResults, setWatchlistSearchResults] = useState({});

  // Fetch watchlists from backend
  const fetchWatchlists = async () => {
    try {
      const res = await fetch(`${API}/watchlists/`, {
        headers: { 'Content-Type': 'application/json', ...authHeaders() },
      });
      if (!res.ok) {
        console.error('Failed to fetch watchlists');
        return;
      }
      const data = await res.json();
      const normalized = data.map(wl => ({ ...wl, items: wl.items || [] }));
      setWatchlists(normalized);
    } catch (err) {
      console.error(err);
    }
  };

  // Load watchlists on mount
  useEffect(() => {
    fetchWatchlists();
  }, []);

  // Search for companies (main search)
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (!value) return setCompanies([]);
    try {
      const res = await fetch(`${API}/search-stock/?q=${encodeURIComponent(value)}`);
      const data = await res.json();
      setCompanies(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  };

  // Get company details
  const handleSelectCompany = async (symbol) => {
    setQuery('');
    setCompanies([]);
    try {
      const res = await fetch(`${API}/company/${encodeURIComponent(symbol)}/`);
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  // Create new watchlist
  const handleCreateWatchlist = async () => {
    if (!newWatchlistName.trim()) return;
    try {
      const res = await fetch(`${API}/watchlists/create/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', ...authHeaders() },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      const created = await res.json();
      if (!res.ok) {
        alert(created.error || 'Failed to create watchlist');
        return;
      }
      const newList = { ...created, items: created.items || [] };
      setWatchlists(prev => [...prev, newList]);
      setNewWatchlistName('');
    } catch (err) {
      console.error(err);
    }
  };

  // Mini search in watchlist
  const handleWatchlistSearch = async (watchlistId, value) => {
    setWatchlistSearch(prev => ({ ...prev, [watchlistId]: value }));
    if (!value) {
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
      return;
    }
    try {
      const res = await fetch(`${API}/search-stock/?q=${encodeURIComponent(value)}`);
      const data = await res.json();
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: Array.isArray(data) ? data : [] }));
    } catch (err) {
      console.error(err);
    }
  };

  // Add company to watchlist
  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      const res = await fetch(`${API}/watchlists/${watchlistId}/add/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', ...authHeaders() },
        body: JSON.stringify(company),
      });
      const updatedList = await res.json();
      if (!res.ok || !updatedList?.id) {
        alert(updatedList.error || 'Failed to add to watchlist');
        return;
      }
      setWatchlists(prev => prev.map(wl => (wl.id === updatedList.id ? updatedList : wl)));
      setWatchlistSearch(prev => ({ ...prev, [watchlistId]: '' }));
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    } catch (err) {
      console.error(err);
    }
  };

  // Remove company from watchlist
  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      const res = await fetch(`${API}/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: 'DELETE',
        headers: { ...authHeaders() },
      });
      const updatedList = await res.json();
      if (!res.ok || !updatedList?.id) {
        alert(updatedList.error || 'Failed to remove from watchlist');
        return;
      }
      setWatchlists(prev => prev.map(wl => (wl.id === updatedList.id ? updatedList : wl)));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dashboard</h1>

      {/* Top Search Bar */}
      <div style={{ marginBottom: "30px" }}>
        <input
          type="text"
          placeholder="Search for a company..."
          value={query}
          onChange={handleSearch}
          style={{ padding: "10px", width: "300px" }}
        />
        {companies.length > 0 && (
          <ul style={{
            border: "1px solid #ccc",
            borderRadius: "4px",
            marginTop: "5px",
            maxHeight: "200px",
            overflowY: "auto",
            width: "300px",
            background: "#fff",
            position: "absolute",
            zIndex: 1000
          }}>
            {companies.map((c, index) => (
              <li
                key={`${c.symbol}-${index}`}
                style={{ padding: "8px", cursor: "pointer" }}
                onClick={() => handleSelectCompany(c.symbol)}
              >
                {c.symbol} - {c.name} ({c.exchange})
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Selected Company Details */}
      {selectedCompany?.price && (
        <div style={{ marginBottom: "30px" }}>
          <h2>{selectedCompany.price.longName} ({selectedCompany.price.symbol})</h2>
          <p><strong>Exchange:</strong> {selectedCompany.price.exchangeName}</p>
          <p><strong>Current Price:</strong> ${selectedCompany.price.regularMarketPrice}</p>
          <p><strong>Sector:</strong> {selectedCompany.summaryProfile?.sector}</p>
          <p><strong>Industry:</strong> {selectedCompany.summaryProfile?.industry}</p>
          <p><strong>Description:</strong> {selectedCompany.summaryProfile?.longBusinessSummary}</p>
        </div>
      )}

      {/* Watchlists */}
      <div>
        <h2>Watchlists</h2>
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="New watchlist name"
            value={newWatchlistName}
            onChange={(e) => setNewWatchlistName(e.target.value)}
          />
          <button onClick={handleCreateWatchlist} style={{ marginLeft: "5px" }}>+ Add Watchlist</button>
        </div>

        {watchlists.map(wl => (
          <div key={wl.id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}>
            <h3>{wl.name}</h3>

            {/* Mini Search */}
            <input
              type="text"
              placeholder="Search company to add..."
              value={watchlistSearch[wl.id] || ''}
              onChange={(e) => handleWatchlistSearch(wl.id, e.target.value)}
              style={{ padding: "5px", width: "250px", marginBottom: "5px" }}
            />
            {watchlistSearchResults[wl.id]?.length > 0 && (
              <ul style={{ border: "1px solid #ccc", maxHeight: "150px", overflowY: "auto", background: "#fff" }}>
                {watchlistSearchResults[wl.id].map((c, idx) => (
                  <li key={`${wl.id}-${c.symbol}-${idx}`} style={{ display: "flex", justifyContent: "space-between", padding: "5px" }}>
                    <span>{c.symbol} - {c.name}</span>
                    <button onClick={() => handleAddToWatchlist(wl.id, c)}>+</button>
                  </li>
                ))}
              </ul>
            )}

            {/* Watchlist Items */}
            <ul style={{ marginTop: "10px" }}>
              {wl.items.map(item => (
                <li key={item.id} style={{ display: "flex", justifyContent: "space-between", padding: "5px", borderBottom: "1px solid #eee" }}>
                  <span>{item.symbol} - {item.name}</span>
                  <button onClick={() => handleRemoveFromWatchlist(wl.id, item.id)}>Remove</button>
                </li>
              ))}
              {wl.items.length === 0 && <li style={{ color: '#777' }}>No companies yet. Use the search above to add some.</li>}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
