import React, { useState, useEffect } from "react";

export default function Dashboard() {
  const [query, setQuery] = useState("");
  const [companies, setCompanies] = useState([]);
  const [watchlists, setWatchlists] = useState([]);
  const [selectedWatchlist, setSelectedWatchlist] = useState(null);

  // Fetch all watchlists
  const fetchWatchlists = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/watchlists/");
      const data = await res.json();
      setWatchlists(data);
      if (!selectedWatchlist && data.length) setSelectedWatchlist(data[0]);
    } catch (error) {
      console.error("Error fetching watchlists:", error);
    }
  };

  useEffect(() => {
    fetchWatchlists();
  }, []);

  // Create a new watchlist
  const handleCreateWatchlist = async () => {
    const name = prompt("Enter watchlist name:");
    if (!name) return;
    try {
      await fetch("http://localhost:5000/api/watchlists/create/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name }),
      });
      fetchWatchlists();
    } catch (error) {
      console.error("Error creating watchlist:", error);
    }
  };

  // Search companies
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (value.length < 2) return setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`);
      const data = await res.json();
      setCompanies(data);
    } catch (error) {
      console.error("Error searching companies:", error);
    }
  };

  // Add company to selected watchlist
  const handleAddToWatchlist = async (company) => {
    if (!selectedWatchlist) return alert("Select a watchlist first!");
    try {
      await fetch(`http://localhost:5000/api/watchlists/${selectedWatchlist.id}/add/`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(company),
      });
      fetchWatchlists();
      setQuery("");
      setCompanies([]);
    } catch (error) {
      console.error("Error adding to watchlist:", error);
    }
  };

  // Remove company from watchlist
  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: "DELETE",
      });
      fetchWatchlists();
    } catch (error) {
      console.error("Error removing from watchlist:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dashboard</h1>

      {/* New Watchlist Button */}
      <div style={{ marginBottom: "20px" }}>
        <button onClick={handleCreateWatchlist}>+ New Watchlist</button>
      </div>

      <div style={{ display: "flex", gap: "20px" }}>
        {/* Watchlist sidebar */}
        <div>
          <h2>Watchlists</h2>
          {watchlists.map((wl) => (
            <div
              key={wl.id}
              style={{
                cursor: "pointer",
                fontWeight: selectedWatchlist?.id === wl.id ? "bold" : "normal",
              }}
              onClick={() => setSelectedWatchlist(wl)}
            >
              {wl.name}
            </div>
          ))}
        </div>

        {/* Selected Watchlist Content */}
        <div style={{ flex: 1 }}>
          {selectedWatchlist && (
            <>
              <h2>{selectedWatchlist.name}</h2>

              {/* Search Bar */}
              <input
                type="text"
                placeholder="Search companies..."
                value={query}
                onChange={handleSearch}
                style={{ padding: "5px", width: "300px", marginBottom: "10px" }}
              />

              {/* Search Results */}
              {companies.length > 0 && (
                <ul
                  style={{
                    border: "1px solid #ccc",
                    maxHeight: "200px",
                    overflowY: "auto",
                    marginBottom: "10px",
                  }}
                >
                  {companies.map((c, i) => (
                    <li
                      key={i}
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        padding: "5px",
                        borderBottom: "1px solid #eee",
                      }}
                    >
                      <span>
                        {c.symbol} - {c.name}
                      </span>
                      <button onClick={() => handleAddToWatchlist(c)}>+</button>
                    </li>
                  ))}
                </ul>
              )}

              {/* Watchlist Items */}
              <ul>
                {selectedWatchlist.items.map((item) => (
                  <li
                    key={item.id}
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      padding: "5px",
                      borderBottom: "1px solid #eee",
                    }}
                  >
                    <span>
                      {item.symbol} - {item.name}
                    </span>
                    <button onClick={() => handleRemoveFromWatchlist(selectedWatchlist.id, item.id)}>
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
