import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiSearch, FiPlus, FiTrash2 } from 'react-icons/fi'; // icons
import { FaCoins } from 'react-icons/fa';

export default function Dashboard() {
  const [query, setQuery] = useState('');
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState('');
  const [watchlistSearch, setWatchlistSearch] = useState({});
  const [watchlistSearchResults, setWatchlistSearchResults] = useState({});

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => { if (!token) navigate('/login'); }, [token, navigate]);
  useEffect(() => { fetchWatchlists(); }, []);

  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (!value) return setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setCompanies(data);
    } catch (err) { console.error(err); }
  };

  const handleSelectCompany = async (symbol) => {
    setQuery(''); setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/company/${symbol}/`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) { console.error(err); }
  };

  const fetchWatchlists = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      const normalized = data.map(wl => ({ ...wl, items: wl.items || [] }));
      setWatchlists(normalized);
    } catch (err) { console.error(err); }
  };

  const handleCreateWatchlist = async () => {
    if (!newWatchlistName.trim()) return;
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/create/`, {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      const newWatchlist = await res.json();
      setWatchlists(prev => [...prev, { ...newWatchlist, items: [] }]);
      setNewWatchlistName('');
    } catch (err) { console.error(err); }
  };

  const handleWatchlistSearch = async (watchlistId, value) => {
    setWatchlistSearch(prev => ({ ...prev, [watchlistId]: value }));
    if (!value) return setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: data }));
    } catch (err) { console.error(err); }
  };

  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/add/`, {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(company),
      });
      const addedCompany = await res.json();
      setWatchlists(prev =>
        prev.map(wl => wl.id === watchlistId ? { ...wl, items: [...wl.items, addedCompany] } : wl)
      );
      setWatchlistSearch(prev => ({ ...prev, [watchlistId]: '' }));
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    } catch (err) { console.error(err); }
  };

  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` }
      });
      setWatchlists(prev =>
        prev.map(wl => wl.id === watchlistId ? { ...wl, items: wl.items.filter(item => item.id !== itemId) } : wl)
      );
    } catch (err) { console.error(err); }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 p-8 font-sans">
      <h1 className="text-4xl font-bold mb-6 text-purple-800">ðŸ“Š Dashboard</h1>

      {/* Global Search */}
      <div className="relative mb-10">
        <div className="flex items-center border border-gray-300 rounded-lg bg-white shadow-sm overflow-hidden w-96">
          <FiSearch className="ml-3 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Search for a company..."
            value={query}
            onChange={handleSearch}
            className="px-3 py-2 w-full focus:outline-none"
          />
        </div>

        {companies.length > 0 && (
          <ul className="absolute mt-1 w-96 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-y-auto z-50">
            {companies.map((c, idx) => (
              <li
                key={idx}
                className="px-4 py-2 hover:bg-purple-100 cursor-pointer"
                onClick={() => handleSelectCompany(c.symbol)}
              >
                <span className="font-semibold">{c.symbol}</span> - {c.name} <span className="text-gray-500">({c.exchange})</span>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Company Details */}
      {selectedCompany?.price && (
        <div className="bg-white shadow-md rounded-xl p-6 mb-10 border-l-4 border-purple-500">
          <h2 className="text-2xl font-bold mb-2 text-purple-700">{selectedCompany.price.longName} ({selectedCompany.price.symbol})</h2>
          <p className="mb-1"><strong>Exchange:</strong> {selectedCompany.price.exchangeName}</p>
          <p className="mb-1"><strong>Current Price:</strong> <span className="text-green-600 font-semibold">${selectedCompany.price.regularMarketPrice}</span></p>
          <p className="mb-1"><strong>Sector:</strong> {selectedCompany.summaryProfile?.sector}</p>
          <p className="mb-1"><strong>Industry:</strong> {selectedCompany.summaryProfile?.industry}</p>
          <p className="mt-2 text-gray-700">{selectedCompany.summaryProfile?.longBusinessSummary}</p>
        </div>
      )}

      {/* Watchlists */}
      <div>
        <div className="flex items-center mb-4">
          <input
            type="text"
            placeholder="New watchlist name"
            value={newWatchlistName}
            onChange={(e) => setNewWatchlistName(e.target.value)}
            className="px-3 py-2 border border-gray-300 rounded-lg w-64 focus:outline-none"
          />
          <button
            onClick={handleCreateWatchlist}
            className="ml-3 bg-purple-600 text-white px-4 py-2 rounded-lg flex items-center hover:bg-purple-700 transition"
          >
            <FiPlus className="mr-2" /> Add Watchlist
          </button>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {watchlists.map(wl => (
            <div key={wl.id} className="bg-white p-4 rounded-xl shadow-md border-t-4 border-purple-500">
              <h3 className="text-xl font-bold mb-3 flex items-center"><FaCoins className="mr-2 text-yellow-500"/> {wl.name}</h3>

              {/* Mini Search */}
              <div className="relative mb-2">
                <input
                  type="text"
                  placeholder="Search company to add..."
                  value={watchlistSearch[wl.id] || ''}
                  onChange={(e) => handleWatchlistSearch(wl.id, e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-lg w-full focus:outline-none"
                />
                {watchlistSearchResults[wl.id]?.length > 0 && (
                  <ul className="absolute mt-1 w-full bg-white border border-gray-300 rounded-lg shadow-lg max-h-48 overflow-y-auto z-50">
                    {watchlistSearchResults[wl.id].map((c, idx) => (
                      <li key={idx} className="flex justify-between px-3 py-2 hover:bg-purple-100 cursor-pointer">
                        <span>{c.symbol} - {c.name}</span>
                        <button
                          onClick={() => handleAddToWatchlist(wl.id, c)}
                          className="text-green-600 hover:text-green-800"
                        ><FiPlus /></button>
                      </li>
                    ))}
                  </ul>
                )}
              </div>

              {/* Items */}
              <ul className="mt-2">
                {wl.items.map(item => (
                  <li key={item.id} className="flex justify-between items-center px-2 py-1 border-b border-gray-100">
                    <span>{item.symbol} - {item.name}</span>
                    <button
                      onClick={() => handleRemoveFromWatchlist(wl.id, item.id)}
                      className="text-red-600 hover:text-red-800"
                    ><FiTrash2 /></button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
