import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import Dashboard from './Dashboard';

export default function App() {
  return (
    <Router>
      {/* Background */}
      <div className="h-screen w-screen bg-gradient-to-br from-purple-700 via-blue-600 to-indigo-800 flex items-center justify-center relative overflow-hidden">
        {/* Decorative circles */}
        <div className="absolute top-0 left-0 w-72 h-72 bg-pink-400 rounded-full mix-blend-multiply filter blur-3xl opacity-40 animate-blob"></div>
        <div className="absolute bottom-0 right-0 w-72 h-72 bg-yellow-400 rounded-full mix-blend-multiply filter blur-3xl opacity-40 animate-blob animation-delay-2000"></div>
        <div className="absolute top-1/2 left-1/2 w-72 h-72 bg-green-400 rounded-full mix-blend-multiply filter blur-3xl opacity-40 animate-blob animation-delay-4000"></div>

        {/* Main container */}
        <div className="z-10 w-full max-w-2xl mx-auto text-center px-6">
          <h1 className="text-white text-5xl font-extrabold mb-8 drop-shadow-lg">
            Welcome to <span className="text-yellow-300">Auth Portal</span>
          </h1>

          {/* Navigation */}
          <nav className="flex justify-center gap-6 mb-10">
            <Link
              to="/login"
              className="px-6 py-3 rounded-lg bg-gradient-to-r from-blue-500 to-indigo-600 text-white font-semibold shadow-md hover:shadow-lg hover:scale-105 transition-all"
            >
              Login
            </Link>
            <Link
              to="/register"
              className="px-6 py-3 rounded-lg bg-gradient-to-r from-green-500 to-emerald-600 text-white font-semibold shadow-md hover:shadow-lg hover:scale-105 transition-all"
            >
              Register
            </Link>
          </nav>

          {/* Glass card */}
          <div className="bg-white/20 backdrop-blur-lg rounded-xl shadow-2xl p-8 border border-white/30">
            <Routes>
              <Route path="/" element={<Navigate to="/login" />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/dashboard" element={<Dashboard />} />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}
