import React, { useState, useEffect } from 'react';

export default function Dashboard() {
  const [query, setQuery] = useState('');
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  // Watchlist states
  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState('');
  const [watchlistSearch, setWatchlistSearch] = useState({}); // { watchlistId: searchQuery }
  const [watchlistSearchResults, setWatchlistSearchResults] = useState({}); // { watchlistId: [companies] }

  // 🔍 Search for companies (global top search)
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (value.length > 0) {
      try {
        const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`);
        const data = await res.json();
        setCompanies(data);
      } catch (err) {
        console.error(err);
      }
    } else {
      setCompanies([]);
    }
  };

  // 📊 Fetch company details
  const handleSelectCompany = async (symbol) => {
    setQuery('');
    setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/company/${symbol}/`);
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  // Watchlist APIs
  const fetchWatchlists = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/watchlists/");
      const data = await res.json();
      setWatchlists(data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchWatchlists();
  }, []);

  // Create new watchlist
  const handleCreateWatchlist = async () => {
    if (!newWatchlistName) return;
    try {
      await fetch("http://localhost:5000/api/watchlists/create/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      setNewWatchlistName('');
      fetchWatchlists();
    } catch (err) {
      console.error(err);
    }
  };

  // Watchlist mini search
  const handleWatchlistSearch = async (watchlistId, value) => {
    setWatchlistSearch(prev => ({ ...prev, [watchlistId]: value }));
    if (value.length < 1) return setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`);
      const data = await res.json();
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: data }));
    } catch (err) {
      console.error(err);
    }
  };

  // Add company to watchlist
  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/add/`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(company),
      });
      fetchWatchlists();
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
      setWatchlistSearch(prev => ({ ...prev, [watchlistId]: '' }));
    } catch (err) {
      console.error(err);
    }
  };

  // Remove company from watchlist
  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: "DELETE",
      });
      fetchWatchlists();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dashboard</h1>

      {/* Top Search Bar */}
      <div style={{ marginBottom: "30px" }}>
        <input
          type="text"
          placeholder="Search for a company..."
          value={query}
          onChange={handleSearch}
          style={{ padding: "10px", width: "300px" }}
        />
        {companies.length > 0 && (
          <ul style={{
            border: "1px solid #ccc",
            borderRadius: "4px",
            marginTop: "5px",
            maxHeight: "200px",
            overflowY: "auto",
            width: "300px",
            background: "#fff",
            position: "absolute",
            zIndex: 1000
          }}>
            {companies.map((c, index) => (
              <li
                key={index}
                style={{ padding: "8px", cursor: "pointer" }}
                onClick={() => handleSelectCompany(c.symbol)}
              >
                {c.symbol} - {c.name} ({c.exchange})
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Selected Company Details */}
      {selectedCompany && selectedCompany.price && (
        <div style={{ marginBottom: "30px" }}>
          <h2>{selectedCompany.price.longName} ({selectedCompany.price.symbol})</h2>
          <p><strong>Exchange:</strong> {selectedCompany.price.exchangeName}</p>
          <p><strong>Current Price:</strong> ${selectedCompany.price.regularMarketPrice}</p>
          <p><strong>Sector:</strong> {selectedCompany.summaryProfile?.sector}</p>
          <p><strong>Industry:</strong> {selectedCompany.summaryProfile?.industry}</p>
          <p><strong>Description:</strong> {selectedCompany.summaryProfile?.longBusinessSummary}</p>
        </div>
      )}

      {/* Watchlists Section */}
      <div>
        <h2>Watchlists</h2>
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="New watchlist name"
            value={newWatchlistName}
            onChange={(e) => setNewWatchlistName(e.target.value)}
          />
          <button onClick={handleCreateWatchlist} style={{ marginLeft: "5px" }}>+ Add Watchlist</button>
        </div>

        {/* Display watchlists */}
        {watchlists.map(wl => (
          <div key={wl.id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}>
            <h3>{wl.name}</h3>

            {/* Mini Watchlist Search */}
            <input
              type="text"
              placeholder="Search company to add..."
              value={watchlistSearch[wl.id] || ''}
              onChange={(e) => handleWatchlistSearch(wl.id, e.target.value)}
              style={{ padding: "5px", width: "250px", marginBottom: "5px" }}
            />
            {watchlistSearchResults[wl.id] && watchlistSearchResults[wl.id].length > 0 && (
              <ul style={{ border: "1px solid #ccc", maxHeight: "150px", overflowY: "auto", background: "#fff" }}>
                {watchlistSearchResults[wl.id].map((c, idx) => (
                  <li key={idx} style={{ display: "flex", justifyContent: "space-between", padding: "5px" }}>
                    <span>{c.symbol} - {c.name}</span>
                    <button onClick={() => handleAddToWatchlist(wl.id, c)}>+</button>
                  </li>
                ))}
              </ul>
            )}

            {/* Display watchlist items */}
            <ul style={{ marginTop: "10px" }}>
              {wl.items.map(item => (
                <li key={item.id} style={{ display: "flex", justifyContent: "space-between", padding: "5px", borderBottom: "1px solid #eee" }}>
                  <span>{item.symbol} - {item.name}</span>
                  <button onClick={() => handleRemoveFromWatchlist(wl.id, item.id)}>Remove</button>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
