import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from "react-router-dom";
import Register from "./Register";
import Login from "./Login";
import Dashboard from "./Dashboard";
import authBg from "./assets/bg-auth.jpg";
import { FaSignInAlt, FaUserPlus } from "react-icons/fa";
import "./App.css";

// Layout for Auth pages
function AuthLayout({ children }) {
  return (
    <div className="app-container" style={{ backgroundImage: `url(${authBg})` }}>
      <div className="app-content">
        <h1 className="app-header">Welcome to Auth Portal</h1>

        {/* Navigation */}
        <nav className="nav-links">
          <Link to="/login" className="flex items-center gap-2">
            <FaSignInAlt /> Login
          </Link>
          <Link to="/register" className="register flex items-center gap-2">
            <FaUserPlus /> Register
          </Link>
        </nav>

        {children}
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            <AuthLayout>
              <Login />
            </AuthLayout>
          }
        />
        <Route
          path="/register"
          element={
            <AuthLayout>
              <Register />
            </AuthLayout>
          }
        />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}
