import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Make sure this CSS file exists

export default function Dashboard() {
  const [query, setQuery] = useState('');
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState('');
  const [watchlistSearch, setWatchlistSearch] = useState({});
  const [watchlistSearchResults, setWatchlistSearchResults] = useState({});

  const [selectedSector, setSelectedSector] = useState("Technology");
  const [selectedWatchlist, setSelectedWatchlist] = useState(null);

  const sectors = [
    "Technology",
    "Healthcare",
    "Financials",
    "Energy",
    "Consumer Discretionary",
    "Utilities",
    "Industrials",
    "Materials",
    "Real Estate",
    "Consumer Staples",
    "Communication Services"
  ];

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  // 🔒 Redirect if not logged in
  useEffect(() => {
    if (!token) {
      navigate('/login');
    }
  }, [token, navigate]);

  // Fetch watchlists when dashboard loads
  useEffect(() => {
    fetchWatchlists();
  }, []);

  // 🔍 Global search
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (!value) return setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setCompanies(data);
    } catch (err) {
      console.error(err);
    }
  };

  // 📊 Select company
  const handleSelectCompany = async (symbol) => {
    setQuery('');
    setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/company/${symbol}/`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  // 📂 Fetch watchlists
  const fetchWatchlists = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      const normalized = data.map(wl => ({ ...wl, items: wl.items || [] }));
      setWatchlists(normalized);
    } catch (err) {
      console.error(err);
    }
  };

  // ➕ Create watchlist
  const handleCreateWatchlist = async () => {
    if (!newWatchlistName.trim()) return;
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/create/`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      const newWatchlist = await res.json();
      setWatchlists(prev => [...prev, { ...newWatchlist, items: [] }]);
      setNewWatchlistName('');
    } catch (err) {
      console.error(err);
    }
  };

  // 🔍 Mini search inside watchlist
  const handleWatchlistSearch = async (watchlistId, value) => {
    setWatchlistSearch(prev => ({ ...prev, [watchlistId]: value }));
    if (!value) return setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: data }));
    } catch (err) {
      console.error(err);
    }
  };

  // ➕ Add company to watchlist
  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/add/`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(company),
      });
      const addedCompany = await res.json();
      setWatchlists(prev =>
        prev.map(wl =>
          wl.id === watchlistId
            ? { ...wl, items: [...wl.items, addedCompany] }
            : wl
        )
      );
      setWatchlistSearch(prev => ({ ...prev, [watchlistId]: '' }));
      setWatchlistSearchResults(prev => ({ ...prev, [watchlistId]: [] }));
    } catch (err) {
      console.error(err);
    }
  };

  // ❌ Remove from watchlist
  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` }
      });
      setWatchlists(prev =>
        prev.map(wl =>
          wl.id === watchlistId
            ? { ...wl, items: wl.items.filter(item => item.id !== itemId) }
            : wl
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  // ⭐ Add 10 random companies by sector
  const handleAddRandomBySector = async () => {
    if (!selectedWatchlist) {
      alert("Please choose a watchlist first!");
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${selectedWatchlist}/add-random/`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ sector: selectedSector }),
      });
      const data = await res.json();
      if (res.ok) {
        setWatchlists(prev =>
          prev.map(wl => wl.id === selectedWatchlist ? data : wl)
        );
      } else {
        alert(data.error || "Failed to add random companies.");
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="dashboard-layout">
      {/* 🔹 Side Panel for Sector Selection */}
      <aside className="side-panel">
        <h2>Add Random Companies</h2>
        <label>Choose Sector:</label>
        <select value={selectedSector} onChange={(e) => setSelectedSector(e.target.value)}>
          {sectors.map(s => <option key={s} value={s}>{s}</option>)}
        </select>

        <label style={{ marginTop: "10px" }}>Choose Watchlist:</label>
        <select value={selectedWatchlist || ''} onChange={(e) => setSelectedWatchlist(Number(e.target.value))}>
          <option value="">-- Select Watchlist --</option>
          {watchlists.map(wl => (
            <option key={wl.id} value={wl.id}>{wl.name}</option>
          ))}
        </select>

        <button onClick={handleAddRandomBySector} style={{ marginTop: "10px" }}>
          ➕ Add 10 Random Companies
        </button>
      </aside>

      <main className="dashboard-container">
        <h1>📈 My Dashboard</h1>

        {/* Global Search */}
        <div className="card">
          <input
            type="text"
            placeholder="Search for a company..."
            value={query}
            onChange={handleSearch}
          />
          {companies.length > 0 && (
            <ul className="search-results">
              {companies.map((c, index) => (
                <li key={index} onClick={() => handleSelectCompany(c.symbol)}>
                  {c.symbol} - {c.name} ({c.exchange})
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Company Details */}
        {selectedCompany?.price && (
          <div className="card">
            <h2>{selectedCompany.price.longName} ({selectedCompany.price.symbol})</h2>
            <p><strong>Exchange:</strong> {selectedCompany.price.exchangeName}</p>
            <p><strong>Current Price:</strong> ${selectedCompany.price.regularMarketPrice}</p>
            <p><strong>Sector:</strong> {selectedCompany.summaryProfile?.sector}</p>
            <p><strong>Industry:</strong> {selectedCompany.summaryProfile?.industry}</p>
            <p><strong>Description:</strong> {selectedCompany.summaryProfile?.longBusinessSummary}</p>
          </div>
        )}

        {/* Watchlists */}
        <div className="card">
          <h2>Watchlists</h2>
          <div style={{ marginBottom: "10px" }}>
            <input
              type="text"
              placeholder="New watchlist name"
              value={newWatchlistName}
              onChange={(e) => setNewWatchlistName(e.target.value)}
            />
            <button onClick={handleCreateWatchlist} style={{ marginLeft: "5px" }}>+ Add Watchlist</button>
          </div>

          {watchlists.map(wl => (
            <div key={wl.id} className="card" style={{ marginBottom: "10px" }}>
              <h3>{wl.name}</h3>

              {/* Mini Search */}
              <input
                type="text"
                placeholder="Search company to add..."
                value={watchlistSearch[wl.id] || ''}
                onChange={(e) => handleWatchlistSearch(wl.id, e.target.value)}
              />
              {watchlistSearchResults[wl.id]?.length > 0 && (
                <ul className="search-results">
                  {watchlistSearchResults[wl.id].map((c, idx) => (
                    <li key={idx}>
                      <span>{c.symbol} - {c.name}</span>
                      <button onClick={() => handleAddToWatchlist(wl.id, c)}>+</button>
                    </li>
                  ))}
                </ul>
              )}

              {/* Items */}
              <ul>
                {wl.items.map(item => (
                  <li key={item.id} className="watchlist-item">
                    <span>{item.symbol} - {item.name}</span>
                    <button onClick={() => handleRemoveFromWatchlist(wl.id, item.id)}>Remove</button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
