// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import Dashboard from './Dashboard';
import bgImage from './assets/bg-auth.jpg'; // Make sure you have this image in src/assets/

export default function App() {
  return (
    <Router>
      <div className="relative min-h-screen w-screen">
        {/* Full-screen background image */}
        <div
          className="fixed inset-0 bg-cover bg-center"
          style={{ backgroundImage: `url(${bgImage})` }}
        ></div>

        {/* Dark overlay */}
        <div className="fixed inset-0 bg-black bg-opacity-60"></div>

        {/* Main content */}
        <div className="relative z-10 flex flex-col items-center justify-center min-h-screen p-8">
          <h1 className="text-transparent bg-clip-text bg-gradient-to-r from-pink-400 via-purple-500 to-indigo-500 text-5xl font-extrabold mb-10 drop-shadow-lg text-center animate-pulse">
            Welcome to Auth Portal
          </h1>

          <nav className="flex space-x-8 mb-12">
            <Link
              to="/login"
              className="text-lg px-6 py-3 rounded-md bg-gradient-to-r from-blue-500 to-blue-700 text-white font-semibold hover:from-blue-600 hover:to-blue-800 hover:scale-105 shadow-lg transition transform duration-300"
            >
              Login
            </Link>
            <Link
              to="/register"
              className="text-lg px-6 py-3 rounded-md bg-gradient-to-r from-green-500 to-emerald-600 text-white font-semibold hover:from-green-600 hover:to-emerald-700 hover:scale-105 shadow-lg transition transform duration-300"
            >
              Register
            </Link>
          </nav>

          <div className="w-full max-w-md bg-white bg-opacity-90 rounded-lg p-8 shadow-2xl backdrop-blur-md">
            <Routes>
              <Route path="/" element={<Navigate to="/login" />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/dashboard" element={<Dashboard />} />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}
