// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaEnvelope, FaLock } from 'react-icons/fa';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // SimpleJWT expects username + password (your username IS email)
      const res = await fetch('http://localhost:5000/api/token/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: email, password }),
      });

      const data = await res.json();
      if (res.ok && data.access) {
        localStorage.setItem('access', data.access);
        localStorage.setItem('refresh', data.refresh);
        navigate('/dashboard');
      } else {
        alert(`❌ ${data.detail || 'Invalid credentials'}`);
      }
    } catch (err) {
      console.error(err);
      alert('❌ Login failed.');
    }
  };

  return (
    <form onSubmit={handleLogin} className="backdrop-blur-md bg-white/30 p-8 rounded-2xl shadow-2xl space-y-5">
      <h2 className="text-3xl font-bold text-white text-center mb-4">Welcome Back</h2>

      <div className="relative">
        <FaEnvelope className="absolute top-3 left-3 text-gray-400" />
        <input
          type="email"
          placeholder="Email"
          className="w-full pl-10 p-2 rounded-lg border border-white/30 bg-white/20 text-white placeholder-white focus:outline-none focus:ring-2 focus:ring-blue-300"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>

      <div className="relative">
        <FaLock className="absolute top-3 left-3 text-gray-400" />
        <input
          type="password"
          placeholder="Password"
          className="w-full pl-10 p-2 rounded-lg border border-white/30 bg-white/20 text-white placeholder-white focus:outline-none focus:ring-2 focus:ring-blue-300"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>

      <button className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-2 rounded-lg hover:scale-105 transition transform duration-300 font-semibold">
        Login
      </button>
    </form>
  );
}
