// src/Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

export default function Dashboard() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  // App state
  const [query, setQuery] = useState("");
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  const [watchlists, setWatchlists] = useState([]);
  const [newWatchlistName, setNewWatchlistName] = useState("");
  const [watchlistSearch, setWatchlistSearch] = useState({});
  const [watchlistSearchResults, setWatchlistSearchResults] = useState({});

  // Sector / add-random state
  const [sectors, setSectors] = useState([]);
  const [selectedSector, setSelectedSector] = useState("");
  const [selectedWatchlist, setSelectedWatchlist] = useState(null);
  const [addingRandom, setAddingRandom] = useState(false);

  // Redirect if not logged in
  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  // Fetch watchlists on load
  useEffect(() => {
    fetchWatchlists();
    fetchSectors();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Fetch watchlists helper (robust to different response shapes)
  const fetchWatchlists = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/watchlists/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (Array.isArray(data)) {
        setWatchlists(data.map((wl) => ({ ...wl, items: wl.items || [] })));
      } else if (data.results && Array.isArray(data.results)) {
        setWatchlists(data.results.map((wl) => ({ ...wl, items: wl.items || [] })));
      } else {
        // Unexpected shape â€” try to be resilient
        console.error("Unexpected watchlists response:", data);
        setWatchlists([]);
      }
    } catch (err) {
      console.error("Failed to fetch watchlists", err);
    }
  };

  // Fetch sectors (from CSV via backend)
  const fetchSectors = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/sectors/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (Array.isArray(data) && data.length > 0) {
        setSectors(data);
        setSelectedSector(data[0]);
      } else {
        setSectors([]);
      }
    } catch (err) {
      console.error("Failed to fetch sectors", err);
      setSectors([]);
    }
  };

  // Global search
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);
    if (!value) {
      setCompanies([]);
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${encodeURIComponent(value)}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setCompanies(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
    }
  };

  // Select company -> get details
  const handleSelectCompany = async (symbol) => {
    setQuery("");
    setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/company/${encodeURIComponent(symbol)}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  // Create watchlist
  const handleCreateWatchlist = async () => {
    if (!newWatchlistName.trim()) return;
    try {
      const res = await fetch("http://localhost:5000/api/watchlists/create/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ name: newWatchlistName }),
      });
      const data = await res.json();
      setWatchlists((prev) => [...prev, { ...data, items: data.items || [] }]);
      setNewWatchlistName("");
    } catch (err) {
      console.error(err);
    }
  };

  // Mini search inside watchlist
  const handleWatchlistSearch = async (watchlistId, value) => {
    setWatchlistSearch((prev) => ({ ...prev, [watchlistId]: value }));
    if (!value) return setWatchlistSearchResults((prev) => ({ ...prev, [watchlistId]: [] }));
    try {
      const res = await fetch(`http://localhost:5000/api/search-stock/?q=${encodeURIComponent(value)}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setWatchlistSearchResults((prev) => ({ ...prev, [watchlistId]: Array.isArray(data) ? data : [] }));
    } catch (err) {
      console.error(err);
    }
  };

  // Add single company to watchlist
  const handleAddToWatchlist = async (watchlistId, company) => {
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/add/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(company),
      });
      const addedCompany = await res.json();
      setWatchlists((prev) =>
        prev.map((wl) => (wl.id === watchlistId ? { ...wl, items: [...(wl.items || []), addedCompany] } : wl))
      );
      setWatchlistSearch((prev) => ({ ...prev, [watchlistId]: "" }));
      setWatchlistSearchResults((prev) => ({ ...prev, [watchlistId]: [] }));
    } catch (err) {
      console.error(err);
    }
  };

  // Remove from watchlist
  const handleRemoveFromWatchlist = async (watchlistId, itemId) => {
    try {
      await fetch(`http://localhost:5000/api/watchlists/${watchlistId}/remove/${itemId}/`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      setWatchlists((prev) =>
        prev.map((wl) => (wl.id === watchlistId ? { ...wl, items: (wl.items || []).filter((it) => it.id !== itemId) } : wl))
      );
    } catch (err) {
      console.error(err);
    }
  };

  // Add 10 random companies by sector (from CSV via backend)
  const handleAddRandomBySector = async () => {
    if (!selectedWatchlist) {
      alert("Please choose a watchlist first!");
      return;
    }
    if (addingRandom) return;
    setAddingRandom(true);
    try {
      const res = await fetch(`http://localhost:5000/api/watchlists/${selectedWatchlist}/add-random/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sector: selectedSector }),
      });
      const data = await res.json();
      if (res.ok) {
        setWatchlists((prev) => prev.map((wl) => (wl.id === selectedWatchlist ? data : wl)));
      } else {
        alert(data.error || "Failed to add random companies.");
      }
    } catch (err) {
      console.error(err);
      alert("Request failed. Please try again.");
    } finally {
      setAddingRandom(false);
    }
  };

  return (
    <div className="dashboard-layout" style={{ display: "flex", gap: 20 }}>
      {/* Side panel */}
      <aside className="side-panel" style={{ width: 260 }}>
        <h2>Add Random Companies</h2>

        <label>Choose Sector</label>
        <select
          value={selectedSector}
          onChange={(e) => setSelectedSector(e.target.value)}
          style={{ width: "100%", marginBottom: 10 }}
        >
          {sectors.length === 0 ? <option value="">No sectors</option> :
            sectors.map((s) => <option key={s} value={s}>{s}</option>)
          }
        </select>

        <label>Choose Watchlist</label>
        <select
          value={selectedWatchlist || ""}
          onChange={(e) => setSelectedWatchlist(Number(e.target.value))}
          style={{ width: "100%", marginBottom: 10 }}
        >
          <option value="">-- Select Watchlist --</option>
          {watchlists.map((wl) => (
            <option key={wl.id} value={wl.id}>{wl.name}</option>
          ))}
        </select>

        <button
          type="button"
          onClick={handleAddRandomBySector}
          disabled={addingRandom || !selectedWatchlist || !selectedSector}
          style={{ width: "100%", padding: "8px 12px", opacity: addingRandom ? 0.7 : 1 }}
        >
          {addingRandom ? "Adding..." : "âž• Add 10 Random Companies"}
        </button>
      </aside>

      {/* Main area */}
      <main className="dashboard-container" style={{ flex: 1 }}>
        <h1>ðŸ“ˆ My Dashboard</h1>

        {/* Global Search */}
        <div className="card">
          <input
            type="text"
            placeholder="Search for a company..."
            value={query}
            onChange={handleSearch}
            style={{ width: "100%", padding: 8 }}
          />
          {companies.length > 0 && (
            <ul className="search-results">
              {companies.map((c, idx) => (
                <li key={idx} onClick={() => handleSelectCompany(c.symbol)} style={{ cursor: "pointer" }}>
                  {c.symbol} - {c.name} ({c.exchange})
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Company Details */}
        {selectedCompany?.price && (
          <div className="card">
            <h2>{selectedCompany.price.longName} ({selectedCompany.price.symbol})</h2>
            <p><strong>Exchange:</strong> {selectedCompany.price.exchangeName}</p>
            <p><strong>Current Price:</strong> ${selectedCompany.price.regularMarketPrice}</p>
            <p><strong>Sector:</strong> {selectedCompany.summaryProfile?.sector}</p>
            <p><strong>Industry:</strong> {selectedCompany.summaryProfile?.industry}</p>
            <p><strong>Description:</strong> {selectedCompany.summaryProfile?.longBusinessSummary}</p>
          </div>
        )}

        {/* Watchlists */}
        <div className="card" style={{ marginTop: 16 }}>
          <h2>Watchlists</h2>
          <div style={{ marginBottom: 10 }}>
            <input
              type="text"
              placeholder="New watchlist name"
              value={newWatchlistName}
              onChange={(e) => setNewWatchlistName(e.target.value)}
            />
            <button onClick={handleCreateWatchlist} style={{ marginLeft: 6 }}>+ Add Watchlist</button>
          </div>

          {watchlists.map((wl) => (
            <div key={wl.id} className="card" style={{ marginBottom: 12 }}>
              <h3>{wl.name}</h3>

              {/* Mini Search */}
              <input
                type="text"
                placeholder="Search company to add..."
                value={watchlistSearch[wl.id] || ""}
                onChange={(e) => handleWatchlistSearch(wl.id, e.target.value)}
                style={{ width: "100%", marginBottom: 6 }}
              />
              {watchlistSearchResults[wl.id]?.length > 0 && (
                <ul className="search-results">
                  {watchlistSearchResults[wl.id].map((c, idx) => (
                    <li key={idx}>
                      <span>{c.symbol} - {c.name}</span>
                      <button onClick={() => handleAddToWatchlist(wl.id, c)} style={{ marginLeft: 8 }}>+</button>
                    </li>
                  ))}
                </ul>
              )}

              {/* Items */}
              <ul>
                {(wl.items || []).map((item) => (
                  <li key={item.id} className="watchlist-item" style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                    <span>{item.symbol} - {item.name}</span>
                    <button onClick={() => handleRemoveFromWatchlist(wl.id, item.id)}>Remove</button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
