import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useLocation } from "react-router-dom";
import Register from "./Register";
import Login from "./Login";
import Dashboard from "./Dashboard";
import authBg from "./assets/bg-auth.jpg";
import "./App.css";

function Layout() {
  const location = useLocation();

  // Show header + nav only on login/register pages
  const showAuthHeader = location.pathname === "/login" || location.pathname === "/register";

  return (
    <div
      className="app-container"
      style={{ backgroundImage: `url(${authBg})` }}
    >
      {showAuthHeader && (
        <>
          <h1 className="app-header">Welcome to Auth Portal</h1>

          <nav className="nav-links">
            <Link to="/login">Login</Link>
            <Link to="/register" className="register">
              Register
            </Link>
          </nav>
        </>
      )}

      <Routes>
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Layout />
    </Router>
  );
}
