// Dashboard.js
import React, { useState } from 'react';

export default function Dashboard() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setResults([]);

    try {
      const res = await fetch(`http://localhost:5000/search-stock?q=${encodeURIComponent(query)}`);
      const data = await res.json();
      setResults(data.quotes || []);
    } catch (err) {
      console.error(err);
      alert('❌ Failed to fetch stock data.');
    }

    setLoading(false);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ fontSize: '28px', marginBottom: '20px' }}>Dashboard</h1>

      {/* Search bar */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search for a company or ticker..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={{
            padding: '10px',
            width: '250px',
            marginRight: '10px',
            borderRadius: '4px',
            border: '1px solid #ccc'
          }}
        />
        <button
          onClick={handleSearch}
          style={{
            padding: '10px 15px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Search
        </button>
      </div>

      {/* Loading state */}
      {loading && <p>Loading...</p>}

      {/* Search results */}
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {results.map((item) => (
          <li key={item.symbol} style={{ marginBottom: '8px' }}>
            <strong>{item.symbol}</strong> — {item.shortname || item.longname}
          </li>
        ))}
      </ul>
    </div>
  );
}
