import React, { useState } from 'react';

export default function Dashboard() {
  const [query, setQuery] = useState('');
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  // 🔍 Search for companies
  const handleSearch = async (e) => {
    const value = e.target.value;
    setQuery(value);

    if (value.length > 1) {
      try {
        const res = await fetch(`http://localhost:5000/api/search-stock/?q=${value}`);
        const data = await res.json();
        setCompanies(data);
      } catch (err) {
        console.error(err);
      }
    } else {
      setCompanies([]);
      setSelectedCompany(null); // Clear company details if query is too short
    }
  };

  // 📊 Fetch company details
  const handleSelectCompany = async (symbol) => {
    setQuery('');
    setCompanies([]);
    try {
      const res = await fetch(`http://localhost:5000/api/company/${symbol}/`);
      const data = await res.json();
      setSelectedCompany(data);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Dashboard</h1>

      {/* Search input */}
      <input
        type="text"
        placeholder="Search for a company..."
        value={query}
        onChange={handleSearch}
        style={{ padding: '10px', width: '300px' }}
      />

      {/* Dropdown list */}
      {companies.length > 0 && (
        <ul
          style={{
            border: '1px solid #ccc',
            borderRadius: '4px',
            marginTop: '5px',
            maxHeight: '200px',
            overflowY: 'auto',
            width: '300px',
            background: '#fff',
            position: 'absolute',
            zIndex: 1000,
            listStyleType: 'none',
            padding: 0,
          }}
        >
          {companies.map((c, index) => (
            <li
              key={index}
              style={{ padding: '8px', cursor: 'pointer' }}
              onClick={() => handleSelectCompany(c.symbol)}
            >
              {c.symbol} - {c.name} ({c.exchange})
            </li>
          ))}
        </ul>
      )}

      {/* Company details */}
      {selectedCompany && selectedCompany.price !== undefined && (
        <div style={{ marginTop: '30px' }}>
          <h2>{selectedCompany.symbol}</h2>
          <p>
            <strong>Exchange:</strong> {selectedCompany.exchange || 'N/A'}
          </p>
          <p>
            <strong>Current Price:</strong> ${selectedCompany.price}
          </p>
          <p>
            <strong>Sector:</strong> {selectedCompany.sector || 'N/A'}
          </p>
          <p>
            <strong>Industry:</strong> {selectedCompany.industry || 'N/A'}
          </p>
          <p>
            <strong>Description:</strong> {selectedCompany.description || 'N/A'}
          </p>
        </div>
      )}
    </div>
  );
}
